import { useState } from "react";

import Person from "./Person";
import InputNumber from "./InputNumber";

function SearchFilter ({ searchStr, setSearchStr}) {
  return (
    <div>
      Filter shown with <input
      value={searchStr}
      onChange={(event) => setSearchStr(event.target.value)}
      />
    </div>
  )
};

function AddPerson({persons, setPersons, newName, setNewName, newNumber, setNewNumber, searchStr}) {
  const submitHandler = (event) => {
    event.preventDefault();

    const duplicate = persons.find(person => person.name === newName);

    if(duplicate) alert(`${newName} already exists in the phonebook`);
    else {
      const newPerson = { 
        name: newName,
        number: newNumber
       };
      setPersons(persons.concat(newPerson));
    }
  };
  return (
    <form onSubmit={submitHandler}>
      <InputNumber label={'name'} newAttribute={newName} setNewAttribute={setNewName}/>
      <InputNumber label={'number'} newAttribute={newNumber} setNewAttribute={setNewNumber}/>
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  )};

function App() {
  ////////establish state//////////
  const [persons, setPersons] = useState([
      { 
        name: 'Arto Hellas',
        number: '354-03495'
      }
  ]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [searchStr, setSearchStr] = useState('');
  /////////////////////////////////////////////

  return (
    <div>
      <h2>Phonebook</h2>
      <AddPerson persons={persons} setPersons={setPersons} 
                 newName={newName} setNewName={setNewName} 
                 newNumber={newNumber} setNewNumber={setNewNumber} 
                 searchStr={searchStr}
      />
      <h2>Numbers</h2>
      <div>
        {persons.map(person => <Person key={person.name} person={person} />)}
      </div>
    </div>
  );
}

export default App;
