const InputNumber = ({ newNumber, setNewNumber}) => {
    const changeHandler = (event) => setNewNumber(event.target.value);
    return (
        <form>
            <div>
                number: <input
                value={newNumber}
                onChange={changeHandler} />
            </div>
        </form>
    );
};

export default InputNumber;
